package assignment;

public class MultipleOf3WithoutUsingOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=377;
		boolean b=true;
		for(int i=0;i<=a;i+=3) {
			if(a==i) {
				b=false;}}
			if(!b) {System.out.println("MULTIPLE OF THREE");}
		else {System.out.print("NOT MULTIPLE OF THREE");
		}
	}

}
