package decision_making;

public class OddorEven {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       int num=5;
       if (num%2>=0)
       {
    	   System.out.println("Odd number");
       }
       else
       {
    	   System.out.println("Even number");
       }
	}

}
