package switchcase;

public class Demo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int n=5;
        int r=n%2;
        switch (r)
        {
        case 0:
        System.out.println("Even");
        break;
        case 1:
        System.out.println("Odd");
        }
	}

}
